package com.viettel.etc.repositories.tables;import com.viettel.etc.repositories.tables.entities.WorkingDaysEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.sql.Date;import java.util.List;/** * Autogen class Repository Interface: Create Repository For Table Name Working_days * * @author ToolGen * @date Sun Nov 21 11:59:24 ICT 2021 */@Repositorypublic interface WorkingDaysRepositoryJPA extends JpaRepository<WorkingDaysEntity, Long> {    @Query("SELECT wd FROM WorkingDaysEntity wd WHERE wd.yyyy = :yyyy AND wd.isLeave = :isLeave")    List<WorkingDaysEntity> findAllByDayOff(Long yyyy, Long isLeave);    @Query("SELECT count(wd) FROM WorkingDaysEntity wd WHERE wd.yyyy = :yyyy AND wd.isLeave = :isLeave and wd.dd > current_date - 1  and wd.dd <= :dd")    int countAllByYyyyAndIsLeave(Long yyyy, Long isLeave, Date dd);}